$(window).on('load resize', function () {
    var w = $(window).width();
    var x = 883;
    var $it = $("#list-top");
    if (w <= x) {
        $lit.hide().off("mouseover").off("mouseout");
    } else {
        $lit.mouseover(function () {
            $("#sub-list", this).stop().slideDown();
        })
        $lit.mouseout(function () {
            $("#sub-list", this).stop().slideUp();
        });
    }
    $("#sub-list").hide();

});
