$(window).on('load resize', function () {
    var w = $(window).width();
    var x = 883;
    var $hil = $(".hil");
    if (w <= x) {
       $hil.off("mouseover").off("mouseout");
    } else {
        $hil.mouseover(function () {
            $("#sub-list", this).stop().slideDown();
        })
        $hil.mouseout(function () {
            $("#sub-list", this).stop().slideUp();
        });
    }
    $("a").removeClass('li-hover');
    $("#sub-list").hide();

});
$(function(){
    $("#top").click(function () {
        $("html,body").animate({
            "scrollTop": 0
        }, 1000, 'swing');
    });
    $('.service').click(function() {
     var service = $('#service').offset().top;
    $('html, body').animate({scrollTop:service});
 
    var $hli = $(".hil");
    var $link = $(".link");
    var $menu1 = $('#menu-icon1 ')

    $(".menu-icon").click(function () {
        $(this).toggleClass("close");
        $hli.slideToggle();
    });

    $link.click(function () {
        $(".icon").removeClass("minus");
        $('.menu-icon ').removeClass('close');
        $hli.slideUp();
        $("#sub-list").slideUp();
        $menu1.find("p").text("＋");
    });

    $menu1.click(function () {
        $("#sub-list").slideToggle();
    });
    $menu1.click(function () {
        var $icon = $(this).find('.icon');
        if ($icon.hasClass('minus')) {
            $icon.removeClass('minus');
            // 子要素の＃タグの中身をtextメソッドを用いて書き換えてください
            $(this).find("p").text("＋");
        } else {
            $icon.addClass('minus');
            // 子要素の＃タグの中身をtextメソッドを用いて書き換えてください
            $(this).find("p").text("－");
        }
    });

});
});
