$(window).on('load resize', function () {
    var w = $(window).width();
    var x = 883;
    var $hil = $(".hil");
    if (w <= x) {
       $hil.hide().off("mouseover").off("mouseout");
    } else {
        $hil.mouseover(function () {
            $("#sub-list", this).stop().slideDown();
        })
        $hil.mouseout(function () {
            $("#sub-list", this).stop().slideUp();
        });
    }
    $("#sub-list").hide();

});
$(function () {
    $("#top").click(function () {
        $("html,body").animate({
            "scrollTop": 0
        }, 1000, 'swing');
    });
});
